			var name = $("input[name='name']").val()
			var otp_phone = $("input[name='otp_phone']").val()
			var lead_id = $('#lead_id').val()

			let url = window.location.href
			let utmSource = getUrlParameter('utm_source', url);
			let utmCampaign = getUrlParameter('utm_campaign', url);


			var formData = new FormData();
			formData.append('phone', phone);
			formData.append('otp_phone', otp_phone);
			formData.append('lead_id', lead_id);
			formData.append('utmSource', utmSource);
			formData.append('utmCampaign', utmCampaign);
			formData.append('link', url);
			formData.append('name', name);

			$.ajax({
				url: _url.base_url + 'Ladipage_lead/check_otp',
				type: "POST",
				data: formData,
